CREATE TABLE ele.tb_goods
(
    id bigint(20) PRIMARY KEY NOT NULL COMMENT '主键' AUTO_INCREMENT,
    seller_id varchar(20) COMMENT '商家ID',
    goods_name varchar(100) COMMENT 'SPU名',
    default_item_id bigint(20) COMMENT '默认SKU',
    audit_status varchar(2) COMMENT '状态',
    is_marketable varchar(1) COMMENT '是否上架',
    brand_id bigint(10) COMMENT '品牌',
    caption varchar(100) COMMENT '副标题',
    category1_id bigint(20) COMMENT '一级类目',
    category2_id bigint(10) COMMENT '二级类目',
    category3_id bigint(10) COMMENT '三级类目',
    small_pic varchar(150) COMMENT '小图',
    price decimal(10,2) COMMENT '商城价',
    type_template_id bigint(20) COMMENT '分类模板ID',
    is_enable_spec varchar(1) COMMENT '是否启用规格',
    is_delete varchar(1) COMMENT '是否删除'
);
